    .site-wrapper {
        background-color: var(--Mygray1) !important;
    }
    .ars-appeal-container,
    .ars-sub-app-no-banners-no-tracking-animated.ars-sub-app.visible {
        display: none !important;
    }
    h2,
    a {
        color: var(--MyW) !important;
    }
    a.lSNext,
    a.lSPrev {
        background-image: -moz-linear-gradient(top, var(--Mygray1), var(--Mygray3)) !important;
        border-radius: 25px !important;
    }
    h1,
    a:hover {
        color: var(--Myred1) !important;
    }
    time,
    h4 {
        color: var(--Mygreen1) !important;
    }
    p,
    span {
        color: var(--Mytext) !important;
    }
