@homepageURL  https://danamw.github.com
==/UserStyle==*/

@-moz-document domain("cnn.com") {
    .bx-align {
        display: none !important;
        color: var(--Myred1)
    }
    [id*="sponsored"],
    div[id*="gigyaShareBar"],
    div.user-msg.headerless.user-msg-flexbox {
        display: none !important;
    }
    .el__leafmedia,
