/* ==UserStyle==
@name         Colors
@namespace    http://danamw.github.io/scripts/scripts/Colors.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/Colors.user.css
@description  My Variable Colors For My Scripts
@version      0.1.3
@author       Dana Meli
@homepageURL  https://danamw.github.io
==/UserStyle== */

:root {
    --TwitterBlue: hsl(203, 89%, 53%) !important;
    --TwitterRed: hsl(341, 75%, 51%) !important;
    --TwitterPink: hsl(340, 75%, 36%) !important;
    --MyB: rgb(0, 0, 0);
    --MyW: rgb(255, 255, 255);
    --Myback: rgb(25, 25, 25);
    --Mytext: rgb(229, 229, 229);
    --Myred1: rgb(255, 0, 0);
    --Myred2: rgb(153, 0, 0);
    --Myred3: rgb(76, 0, 0);
    --Mypurple1: rgb(255, 0, 255);
    --Mypurple2: rgb(153, 0, 153);
    --Mypurple3: rgb(76, 0, 76);
    --Mygreen1: rgb(0, 255, 0);
    --Mygreen2: rgb(0, 153, 0);
    --Mygreen3: rgb(0, 76, 0);
    --Myblue1: rgb(0, 0, 255);
    --Myblue2: rgb(0, 0, 153);
    --Myblue3: rgb(0, 0, 76);
    --Mygray1: rgb(127, 127, 127);
    --Mygray2: rgb(76, 76, 76);
    --Mygray3: rgb(38, 38, 38);
    --Myeditback: rgb(38, 38, 38);
    --Mydialog: rgb(63, 63, 63);
    --Myborder: rgb(153, 153, 153);
    --MyX1: rgba(255, 0, 0, 0.4);
    --MyX2: rgba(0, 255, 0, 0.4);
    --MyX3: rgba(0, 0, 255, 0.4);
    --MyX4: rgba(89, 89, 89, 0.4);
    --MyX5: rgba(255, 0, 255, 0.4);
    --MyXB: rgba(0, 0, 0, 0.4);
    --MyXW: rgba(255, 255, 255, 0.4);
    --Progress-bar-color: rgb(255, 0, 0);
    --scrollbar-color: rgb(255, 0, 0) rgb(76, 0, 0);
    --panel-border-radius: 15px;
    --blue-40: #45a1ff;
    --blue-40-a10: rgb(69, 161, 255, 0.1);
    --blue-50: #0a84ff;
    --blue-50-a30: rgba(10, 132, 255, 0.3);
    --blue-60: #0060df;
    --blue-70: #003eaa;
    --blue-80: #002275;
    --grey-20: #ededf0;
    --grey-30: #d7d7db;
    --grey-40: #b1b1b3;
    --grey-60: #4a4a4f;
    --grey-90: #0c0c0d;
    --grey-90-a10: rgba(12, 12, 13, 0.1);
    --grey-90-a20: rgba(12, 12, 13, 0.2);
    --grey-90-a30: rgba(12, 12, 13, 0.3);
    --grey-90-a50: rgba(12, 12, 13, 0.5);
    --grey-90-a60: rgba(12, 12, 13, 0.6);
    --green-50: #30e60b;
    --green-60: #12bc00;
    --green-70: #058b00;
    --green-80: #006504;
    --green-90: #003706;
    --orange-50: #ff9400;
    --purple-70: #6200a4;
    --red-50: #ff0039;
    --red-50-a30: rgba(255, 0, 57, 0.3);
    --red-60: #d70022;
    --red-70: #a4000f;
    --red-80: #5a0002;
    --red-90: #3e0200;
    --yellow-10: #ffff98;
    --yellow-50: #ffe900;
    --yellow-60: #d7b600;
    --yellow-60-a30: rgba(215, 182, 0, 0.3);
    --yellow-70: #a47f00;
    --yellow-80: #715100;
    --yellow-90: #3e2800;
    --shadow-10: 0 1px 4px var(--grey-90-a10);
    --shadow-30: 0 4px 16px var(--grey-90-a10);
    --card-outline-color: rgb(255, 0, 0) !important;
    --in-content-border-focus: rgb(255, 0, 0) !important;
    --primary-background: rgb(25, 25, 25) !important;
    --secondary-background: rgb(25, 25, 25) !important;
    --primary-font-color: rgb(255, 255, 255) !important;
    --highlighted-font-color: rgb(255, 255, 255) !important;
    --primary-border: 1px solid rgb(255, 0, 0) !important;
    --bright-border: 1px solid rgb(255, 0, 0) !important;
    --border-color: rgb(255, 0, 0) !important;
    --bright-border-color: rgb(255, 0, 0) !important;
    --primary-icon-color: rgb(0, 203, 255) !important;
    --faded-bright-border-color: rgba(0, 208, 255, 0.65) !important;
    --toolbar-bgcolor: rgb(38, 38, 38) !important;
