    --back-trans2: rgba(83, 64, 49, 0.8) !important;
    --lwt-accent-color: rgb(255, 0, 0) !important;
    --lwt-accent-color-inactive: rgb(102, 0, 0) !important;
    --in-content-page-color: rgb(28, 28, 28) !important;
    --in-content-page-background: rgb(28, 28, 28) !important;
    --in-content-text-color: rgb(224, 224, 224) !important;
    --in-content-selected-text: rgb(255, 255, 255) !important;
    --in-content-box-background: rgb(0, 0, 0) !important;
    --in-content-box-background-odd: rgb(51, 51, 51) !important;
    --in-content-box-background-hover: rgb(255, 0, 0) !important;
    --in-content-box-background-active: rgb(28, 28, 28) !important;
    --in-content-box-border-color: rgb(224, 224, 224) !important;
    --in-content-item-hover: rgba(219, 0, 0, 0.411) !important;
    --in-content-item-selected: rgb(255, 0, 0) !important;
    --in-content-border-highlight: rgb(255, 0, 0) !important;
    --in-content-border-color: rgb(224, 224, 224) !important;
    --in-content-border-active: var(--blue-50);
    --in-content-border-active-shadow: var(--blue-50-a30);
    --in-content-border-invalid: var(--red-50);
    --in-content-border-invalid-shadow: var(--red-50-a30);
    --in-content-category-outline-focus: 1px dotted rgb(255, 0, 0) !important;
    --in-content-category-text: hsl(0, 0%, 88%) !important;
    --in-content-category-text-active: rgb(255, 0, 0) !important;
    --in-content-category-text-selected: rgb(255, 0, 0) !important;
    --in-content-category-text-selected-active: rgb(255, 0, 0) !important;
    --in-content-category-background-hover: rgb(255, 0, 0) !important;
    --in-content-category-background-active: rgba(219, 10, 10, 0.541) !important;
    --in-content-category-background-selected-hover: rgb(255, 0, 0) !important;
    --in-content-category-background-selected-active: rgba(199, 24, 24, 0.541) !important;
    --in-content-tab-color: rgb(255, 0, 0) !important;
    --in-content-link-color: rgb(178, 0, 0) !important;
    --in-content-link-color-hover: rgb(255, 0, 0) !important;
    --in-content-link-color-active: rgb(255, 0, 0) !important;
    --in-content-link-color-visited: rgb(178, 0, 0) !important;
    --in-content-primary-button-background: rgb(255, 0, 0) !important;
    --in-content-primary-button-background-hover: rgb(255, 0, 0) !important;
    --in-content-primary-button-background-active: rgb(255, 0, 0) !important;
    --in-content-table-border-dark-color: hsl(0, 0%, 11%) !important;
    --in-content-table-header-background: rgb(28, 28, 28) !important;
    --toolbarbutton-icon-fill-attention: rgb(255, 0, 0) !important;
    --lwt-toolbarbutton-icon-fill-attention: rgb(255, 0, 0) !important;
    --panel-disabled-color: rgba(249, 249, 250, 0.5) !important;
    --arrowpanel-background: rgb(76, 76, 76) !important;
    --arrowpanel-color: rgb(255, 255, 255) !important;
    --arrowpanel-border-color: rgb(255, 0, 0) !important;
    --lwt-toolbar-field-background-color: rgb(38, 38, 38) !important;
    --lwt-toolbar-field-color: rgb(249, 249, 250) !important;
    --lwt-toolbar-field-border-color: rgba(249, 249, 250, 0.2) !important;
    --lwt-text-color: rgb(255, 255, 255) !important;
    --cfr-active-color: rgb(255, 0, 0) !important;
    --urlbar-popup-url-color: rgb(255, 0, 0) !important;
    --urlbar-popup-action-color: rgb(84, 255, 0) !important;
    --urlbar-separator-color: rgb(0, 0, 0) !important;
    --chrome-content-separator-color: rgba(249, 249, 250, 0.7) !important;
    --lwt-toolbarbutton-icon-fill: rgba(249, 249, 250, 0.7) !important;
    --autocomplete-popup-background: rgb(38, 38, 38) !important;
    --autocomplete-popup-color: rgb(255, 255, 255) !important;
    --autocomplete-popup-border-color: rgba(249, 249, 250, 0.7) !important;
    --autocomplete-popup-highlight-background: rgb(255, 0, 0) !important;
    --toolbar-non-lwt-bgcolor: rgb(38, 38, 38) !important;
    --toolbar-non-lwt-textcolor: rgb(255, 255, 255) !important;
    --toolbar-non-lwt-bgimage: none;
    --theme-tab-toolbar-background: rgb(38, 38, 38) !important;
    --theme-toolbar-background: rgb(38, 38, 38) !important;
    --theme-selection-background: rgb(255, 0, 0) !important;
    --theme-selection-color: rgb(255, 255, 255) !important;
    --theme-splitter-color: rgb(255, 255, 255) !important;
    --theme-comment: rgb(255, 0, 0) !important;
    --theme-body-background: rgb(25, 25, 25) !important;
    --theme-sidebar-background: rgb(25, 25, 25) !important;
    --theme-contrast-background: rgb(66, 60, 60) !important;
    --theme-body-color: rgb(155, 155, 155) !important;
    --theme-body-color-alt: rgb(254, 76, 76) !important;
    --theme-content-color1: rgb(255, 132, 132) !important;
    --theme-content-color2: rgb(84, 255, 0) !important;
    --theme-content-color3: rgb(0, 182, 255) !important;
    --theme-highlight-blue: rgb(0, 255, 127) !important;
    --theme-highlight-purple: rgb(255, 101, 221) !important;
    --theme-highlight-pink: rgb(223, 107, 107) !important;
    --theme-highlight-red: rgb(255, 101, 101) !important;
    --theme-highlight-orange: rgb(254, 76, 76) !important;
    --theme-highlight-lightorange: rgb(246, 85, 85) !important;
    --theme-highlight-green: rgb(59, 178, 0) !important;
    --theme-highlight-bluegrey: rgb(133, 168, 207) !important;
    --theme-highlight-yellow: rgb(185, 185, 49) !important;
    --in-content-deemphasized-text: var(--grey-40);
    --in-content-box-info-background: rgba(249, 249, 250, 0.15);
