        display: none !important;
    }
    .privy-container,
    .privy-inner-container,
    .privy-tab-container,
    .privy-bottom-center,
    .privy-bottom,
    .privy-center,
    .privy-basic .privy-tab,
    .privy-bottom,
    .privy-center,
    .privy-basic,
    .privy-white-text,
    .privy .privy-popup-container,
    .privy-popup-content-wrap {
        display: none !important;
    }
    * {
        text-decoration: none !important;
        scrollbar-width: thin !important;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
