/* ==UserStyle==
@name         Facebook Dark
@namespace    https://danamw.github.io/scripts/scripts/FacebookDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/FacebookDark.user.css
@version      0.0.7
@description  Facebook Dark
@author       Dana Meli
@homepageURL  https://danamw.github.io
==/UserStyle== */

@-moz-document regexp("http(s)?://(?!business|developers|docs.google).*\.facebook\.com/(?!(v[0-9].[0-9]/)?plugins/|Fplugins|plugins/like).*"),
url-prefix("https://www.messenger.com/") {
    /* --------------------------------------------- */
    /* ----div[id*="hyperfeed_story_id_"]:hover----- */
    /* --------------------------------------------- */
    div[id="u_jsonp_4_0"] {
        background-color: var(--Myback) !important;
    }
    * {
        text-decoration: none !important;
        scrollbar-width: thin !important;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    button[class="_1mf7 _4r1q _4jy0 _4jy3 _4jy1 _51sy selected _42ft"],
    button[class="_1mf7 _4r1q _4jy0 _4jy3 _4jy1 _51sy selected _42ft _42fr"] {
        width: 96px !important;
        float: right;
        margin-top: -10px !important;
        margin-left: -3px !important;
    }
    .uiTooltipX .tooltipContent {
        background-color: var(--Myred2) !important;
        color: var(--MyW) !important;
        border-radius: 5px !important;
        border: 1px ridge var(--Myborder) !important;
    }
    .uiTooltipX .tooltipContent .tooltipText {
        background-color: var(--Myred2) !important;
        color: var(--MyW) !important;
    }
    /*
    div._5jmm._5pat._3lb4.p_1jsay8qpsp,
    div._5jmm._5pat._3lb4.p_1jsay8qpsp.fbpnormal {
        max-height: calc(100% - 0.5%) !important;
        padding-top: 10px !important;
    */
    div[id*="hyperfeed_story_id_"]:hover {
        box-shadow: 0 0 20px 5px var(--Myred1) !important;
    }
    li#navItem_634987924 {
        background-color: var(--Myred2) !important;
    }
    /* --------------------CHANGE WIDTH OF CENTER-------------------- */
    #contentArea {
        width: 800px !important;
        min-width: 800px !important;
        max-width: 800px !important;
    }
    /*
    #mainContainer {
        padding-left: 205px !important;
