    .ddict_div::before {
        border-bottom-color: rgb(60, 60, 60) !important;
    }
    .TnITTtw-help-inside-layout {
        background: rgb(255, 255, 255) !important;
    }
    .TnITTtw-mv-text-part,
    .TnITTtw-main-of-item {
        color: var(--MyB) !important;
    }
    .TnITTtw-util-butt0n {
        background-color: rgba(233, 233, 233, .725) !important;
    }
    span.TnITTtw-highlight {
        background-color: var(--Mytext) !important;
        color: var(--Myback) !important;
        text-shadow: none !important;
    }
    ghost span {
        color: transparent !important;
    }
