/* ==UserStyle==
@name         FreeWareFiles Dark
@namespace    https://danamw.github.io/scripts/scripts/FreeWareFilesDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/FreeWareFilesDark.user.css
@version      0.0.12
@description  FreeWareFiles Dark
@author       Dana Meli
@homepageURL  https://danamw.github.io
==/UserStyle== */

@-moz-document domain("freewarefiles.com") {
    .subscribers-chicklet,
    .fbwidget,
    div[class="admargin pull-right"],
    .gbqfwa,
    .c7wws91lfh,
    div[class^="uwzj890ax"],
    .muyzhs,
    .gbqfwa,
    .adsbygoogle,
    .left-gray-ad-txt,
    .xfqk3ljqq,
    .bv12ig,
    .sjgp2g6 {
        opacity: 0 !important;
    }
    #st-5,
    div[class="admargin pull-right"],
    img[class="alignleft3"],
    .gbqfwa,
    .gbqfwapq,
    script[src^="//pagead2.googlesyndication"],
    ins[class="adsbygoogle programs_adslot_1"],
    .sticky_ads,
    ins[class="adsbygoogle programs_adslot_3"],
    footer {
        display: none !important;
    }
    div[class="avg-left-col"] {
        margin-top: -10px !important;
    }
    /*
    div[class="right-cont-border"] {
        margin-top: -10px !important;
    }
   */
    * {
        text-decoration: none !important;
        scrollbar-width: thin !important;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    .right-cont-border {
        border-top: 5px solid var(--Myred1) !important;
    }
    .page-link {
        margin-left: 5px !important;
    }
    script[src^="adsbygoogle.js"] {
        display: none !important;
    }
    .container {
        empty-cells: hide !important;
    }
    .small-review-box2,
    div[class="date-time-txt"] p,
    .right-top-list,
    .col-xs-8,
    .col-xs-4,
    div[id="top-border-section"] {
        background-color: var(--Mygray2) !important;
        color: var(--MyW) !important;
    }
    div.product-description-holder p,
    div.product-description-holder {
        float: center !important;
        text-align: center !important;
        border-right: 1px solid var(--Myborder) !important;
        border-bottom: 1px solid var(--Myborder) !important;
        border-left: 1px solid var(--Myborder) !important;
    }
    .product-description-holder>ul li>b,
    .product-description-holder li,
    .product-description-holder p,
    div[class="col-md-12"] {
        text-align: center !important;
    }
    section,
    body,
    html {
        -moz-appearance: none;
        -webkit-appearance: none;
        background-image: none !important;
    }
    div[class="space"] {
        border-top: 5px solid var(--Myred1) !important;
    }
    .avg-left-col,
    .cate-right-heading,
    .center-gray-ad-txt,
    .down-heading,
    .cate-right-heading,
    .left-col-top-heading,
    .blue-featured-head,
    .navbar-form,
    #bottom-lower {
        background-color: var(--Myred2) !important;
        color: var(--Myred2) !important;
    }
    .boerder-top2,
    .cate-right-heading,
    .left-col-top-heading,
    .boerder-top,
    .featured-cont-border,
    .wp-news-border,
    .center-gray-ad-txt,
    .right-cont-border-orange,
    .down-heading {
        border-top: 5px solid var(--Myred1) !important;
    }
    .left-col-holder {
        margin-top: -10px !important;
    }
    div[class="avg-left-col"],
    .boerder-top {
        background-color: var(--Myred2) !important;
    }
    div[class="col-sm-12 col-xs-6 padding new-flies-layout"] h1 {
        margin-right: 5px !important;
    }
    div[class="border-table-col"]>p {
        align-items: center !important;
    }
    h1,
    .blue-featured-head,
    h2[class="downloading-right-heading"],
    div[class="downloading-blue-sec"] h2,
    p[class="comments-txt"] {
        text-align: center !important;
        align-items: center !important;
    }
    .blue-featured-head {
        background-color: var(--Myred2) !important;
        text-align: center;
        width: 275px !important;
    }
    .col-sm-12 {
        align-content: center !important;
    }
    .wp-right-col-holder {
        width: 308px !important;
    }
    .screenshot-subtext,
    input[id="inputEmail3"],
    textarea[class="form-control downl-comments"] {
        color: var(--Mytext) !important;
        background-color: var(--Mygray3) !important;
    }
    .newfiles-mid-box,
    div[class="product-description-holder"]>ul>li,
    p[class="prod-txt"]>span,
    p.sear-down-txt span,
    .mid-gray-box {
        color: var(--Mytext) !important;
        background-color: var(--Myback) !important;
    }
    .reviews-mid-box,
    .downloading-facebook-sec,
    .search-outer-holder,
    .search-cont-bord,
    .program-holder,
    div[class="search-cont-bord"]>div>div>.col-xs-4,
    .search-grey-footer,
    .search-cont-border,
    .mid-white-box,
    .product-description-holder,
    h2[class="download-title"]>span,
    div>article>section,
    html>body>div#wrapper>section>div.container {
        color: var(--Mytext) !important;
        background-color: var(--Myback) !important;
    }
    span[class="down-text"],
    p.search-txt span,
    ol.breadcrumb li,
    a[list-group-item] a {
        text-decoration: none !important;
        color: var(--MyW) !important;
    }
    a:not([list-group-item]) {
        text-decoration: none !important;
        color: var(--Myblue1) !important;
        text-shadow: 1px -1px 0 var(--MyW) !important;
    }
    a:hover {
        text-decoration: none !important;
        color: var(--Myred1) !important;
        text-shadow: 1px -1px 0 var(--MyB) !important;
    }
    p[class="size-txt1"] {
        text-decoration: none !important;
    }
    div.col-xs-8 ul.right-top-list a,
    .col-xs-3 {
        color: var(--MyW) !important;
    }
    img[src="https://cdn.freewarefiles.com/img/download-icon.png"],
    img[src^="img/download-icon.png"] {
        filter: drop-shadow(1px -1px 5px var(--Myred1)) invert(50%);
    }
    .cate-download-bg,
    .downloading-grey-sec,
    address,
    article,
    aside,
    blockquote,
    body,
    caption,
    center,
    col,
    colgroup,
    dd,
    dir,
    dl,
    dt,
    fieldset,
    figcaption,
    figure,
    footer,
    form,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    header,
    hgroup,
    hr,
    html,
    legend,
    listing,
    main,
    marquee,
    menu,
    noframes,
    ol,
    plaintext,
    pre,
    section,
    summary,
    table,
    p,
    tbody,
    td,
    tfoot,
    th,
    thead,
    tr,
    nav,
    xmp {
        color: var(--Mytext) !important;
        background-color: var(--Myback) !important;
    }
    div[class="search-download-btn-small"],
    button[class="btn btn-warning subscribe"],
    input[class="btn btn-primary downl-submit"],
    .downloading-link-pannel,
    .page-link,
    a[class*="btn"],
    button {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border-top: 1px solid var(--Myred2) !important;
        border-left: 1px solid var(--Myred2) !important;
        border-bottom: 1px solid var(--Myred3) !important;
        border-right: 1px solid var(--Myred3) !important;
