sourceCode:
/* ==UserStyle==
@name         Google Dark
@namespace    https://danamw.github.io/scripts/scripts/GoogleDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/GoogleDark.user.css
@version      0.1.10
@author       Dana Meli
@description  Google Dark Style.
@homepageURL  https://DanaMW.github.io
@preprocessor default
@var range swidth "Change Search Width"[300, 300, 1000, 1, "px"]
@var range smove "Move Search Bar" [10, 0, 70, 1, "vw"]
@var range bmove "Move The Buttons" [10, 0, 70, 1, "vw"]
@var range cmove "Move The Column" [10, 0, 70, 1, "vw"]
==/UserStyle== */

@-moz-document url-prefix("https://www.google.com/"),
url-prefix("https://ogs.google.com/"),
url-prefix("https://one.google.com/"),
url-prefix("http://www.google.com/"),
url-prefix("https://accounts.google.com/"),
url-prefix("https://plus.google.com/"),
url-prefix("https://policies.google.com/"),
url-prefix("https://myaccount.google.com/"),
url-prefix("https://gstatic.com/"),
url-prefix("https://groups.google.com/") {
    div[id*="maia-footer"],
    #maia-footer,
    body {
        background-color: var(--MyB) !important;
    }
    .SSBXXc {
        left: calc(1vw + var(--smove) - 10vw) !important;
    }
    html[itemtype="http://schema.org/SearchResultsPage"] .tsf-p,
    html[itemtype="http://schema.org/SearchResultsPage"] .A8SBwf {
        position: relative !important;
        width: var(--swidth) !important;
        left: calc(1vw + var(--smove) - 10vw) !important;
    }
    html[itemtype="http://schema.org/SearchResultsPage"] .A8SBwf.sbfc {
        position: relative !important;
        width: var(--swidth) !important;
        left: calc(2vw + var(--smove) - 10vw) !important;
    }
    .sfibbbc,
    div[jsname="RNNXgb"] {
        position: relative !important;
        width: var(--swidth) !important;
        background: transparent !important;
        border: none !important;
    }
    html[itemtype="http://schema.org/WebPage"] div[jsname="RNNXgb"] {
        width: initial !important;
        margin: auto !important;
    }
    .tAcEof,
    #hdtb-msb {
        position: relative !important;
    }
    .tAcEof,
    #hdtb-msb {
        position: sticky !important;
        margin: 0 auto;
        left: calc(1vw + var(--bmove) - 10vw) !important;
    }
    .mw>#rcnt {
        position: relative !important;
        left: calc(1vw + var(--cmove) - 10vw) !important;
    }
    /*
    html[itemtype="http://schema.org/SearchResultsPage"] form[role="search"],
    html[itemtype="http://schema.org/SearchResultsPage"] form[role="search"] {
        position: absolute !important;
        left: calc(1vw + var(--tmove) - 10vw) !important;
    }
    */
    * {
        text-decoration: none !important;
        scrollbar-width: thin;
        scrollbar-color: var(--Myred1) var(--Myred3);
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    .rc,
    .r {
        text-align: center !important;
    }
    .dU5Kl {
        border: none !important;
    }
    .Uh4Mac,
    .nma0Wb,
    .nrAB0c,
    .dU5Kl,
    header,
    header[role="banner"],
    .rg_l,
    .irc_ris,
    .Qc8zh,
    .Qc8zh,
    .irc_t,
    .irc_mic,
    .irc_mutc,
    #irc_bgl,
    #irc_cc,
    #irc-cl,
    .irc_c,
    #irc_bg {
        background-image: none !important;
        background-color: transparent !important;
    }
    .exp-outline {
        border: none !important;
        border-radius: 0px !important;
        position: absolute;
        transition: opacity 150ms ease-in-out, height 150ms ease-in-out;
        width: 100% !important;
        left: 0px !important;
        transform: none !important;
    }
    .qFWqn,
    .gb_F.gb_na,
    .PaEvOc,
    .section-review,
    div[class*="__root"],
    div[class*="__container"],
    .section-subheader,
    .section-omnibox-spacer,
    .section-result,
    .u4DeMc,
    .ikIPKb,
    div[class*="smsauth-interstitial-infobox"],
    .act-tim-txt-cnt>div,
    #timer-stopwatch-container {
        background-image: none !important;
        background-color: transparent !important;
    }
    .exp-outline,
    div.g {
        border-bottom: 2px solid var(--MyX1) !important;
    }
    h1 {
        color: var(--TwitterBlue) !important;
    }
    blockquote,
    li,
    /*h3,*/
    p,
    [role="heading"],
    .WGvvNb,
    .fxgdke {
        color: var(--MyW) !important;
    }
    #irc-cl {
        border: 1px solid var(--MyW) !important;
    }
    .irc_mi {
        border: 3px solid var(--Mygray3) !important;
    }
    .ll0QOb {
        background-color: var(--Myred1) !important;
        border-color: var(--MyB) !important;
    }
    .yrQA5d,
    .ZZ9xL {
        color: var(--Myred1) !important;
    }
    .gsfi {
        border-radius: 25px !important;
    }
    div[role="button"][aria-label="Search"] {
        border: none !important;
        border-radius: 50% !important;
    }
    input[aria-label="Search Google Maps"],
    input[aria-label="Search Google Account"],
    input[aria-label="Search"] {
        text-align: center !important;
        border-radius: 25px !important;
        color: var(--MyW) !important;
        border: 2px solid var(--Myred2) !important;
    }
    input.Ax4B8.ZAGvjd {
        height: 35px !important;
        margin-top: 6px !important;
    }
    #searchbox,
    input[aria-hidden="true"],
    .a4bIc,
    .sbib_b,
    .gstl_0.sbib_a {
        box-shadow: none !important;
        background-color: transparent !important;
    }
    .Mza7yc,
    .UVgjof {
        padding-left: 10px !important;
    }
    scrolling-carousel,
    div[jsname="ixzLGf"],
    .OUZ5W,
    .QnfS4e,
    .v4dQwb,
    #Sva75c,
    .Mza7yc,
    .UVgjof,
    div[role="main"] {
        background-color: hsla(0, 87%, 12%, 0.8) !important;
    }
    #resultStats {
        color: var(--MyW) !important;
    }
