/* ==UserStyle==
@name         Google Drive Dark
@namespace    https://danamw.github.io/scripts/scripts/GoogleDriveDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/GoogleDriveDark.user.css
@version      0.0.18
@author       Dana Meli
@description  Dark Style.
@homepageURL  https://DanaMW.github.io
==/UserStyle== */

@-moz-document domain("drive.google.com") {
    [aria-label="Side panel"],
    .a-ub-Gd,
