    .a-s-Ba-Ak.a-D-B-Ak.Hb-ja-Na,
    .YEXPof,
    html,
    body {
        background-color: var(--Myback) !important;
        color: var(--MyW) !important;
    }
    * {
        text-decoration: none !important;
        scrollbar-width: thin !important;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    svg {
        filter: invert(1) !important;
    }
    div[role="option"]>div,
    .le-Ba-d-j,
    div[role="tab"],
    .h-sb-Ic.h-R-d.a-c-d,
    .h-sb-Ic.h-R-d.h-R-d-rc.a-c-d,
    .h-sb-Ic.h-R-d.a-c-d.a-hd-Il-mhHukc-d,
    .h-sb-Ic.h-w-d,
    .o-Yc-o-T,
    .jGNTYb,
    .a-U-J,
    .h-sb-Ic.h-R-w-d.a-l-S-Pc-w-d,
    div[role="menuitem"],
    a[aria-label="More Google apps"],
    li>a[draggable="false"],
    button:not([aria-label="Search Google Drive"]):not([aria-label="Search options"]) {
