@name         Google Voice Dark
@namespace    https://danamw.github.io/scripts/scripts/GoogleVoiceDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/GoogleVoiceDark.user.css
@version      0.0.20
@author       Dana Meli
@description  Dark Style.
@homepageURL  https://DanaMW.github.io
@preprocessor default
@var range twidth "Change Search Width"[300, 300, 1000, 1, "px"]
@var range tmove "Move Search Bar" [10, 0, 70, 1, "vw"]
==/UserStyle== */

@-moz-document domain("voice.google.com") {
    .I6IB7e-LwH6nd,
