/* ==UserStyle==
@name         Live Reload Text File
@namespace    https://danamw.github.io/scripts/scripts/LiveReloadTextFile.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/LiveReloadTextFile.user.css
@version      0.0.13
@author       Dana Meli
@description  Dark Style Text File.
@homepageURL  https://DanaMW.github.io
@preprocessor default
@var color backcolor "Background Color"#460101
@var color textcolor "Text Color"#FFFFFF
==/UserStyle== */

@-moz-document url-prefix("file:"),
url-prefix("https://danamw.github.io/scripts/scripts/"),
url-prefix("https://danamw.github.io/dev/help/") {
    body {
        background-color: var(--backcolor) !important;
        color: var(--textcolor) !important;
    }
    td.regular {
        color: var(--MyW) !important;
    }
    td.secondary {
        color: var(--Myred1) !important;
    }
    * {
