@author       Dana Meli
@description  Dark Style.
@homepageURL  https://DanaMW.github.io
==/UserStyle== */

@-moz-document domain("onedrive.live.com") {
    .LeftNav-subLinksAsComponent .od-PremiumUpsellBanner.od-PremiumUpsellBanner--cursor {
        display: none !important;
    }
    * {
        text-decoration: none !important;
