    label:hover,
    h1,
    span.od-LeftNav-linkGroupHeaderTitle,
    a:hover {
        color: var(--Myred1) !important;
    }
    .SharerHeader-title,
    h2 {
        color: var(--TwitterBlue) !important;
    }
    div[role="alert"]>div {
        color: var(--TwitterRed) !important;
    }
    div[data-automationid="DetailsRow"],
    .StorageOptions-newPage-plans,
    div[data-bind*="css"] {
        background-color: var(--Myback) !important;
        color: var(--MyW) !important;
    }
    .od-MessageBar-content {
        background-color: var(--Myred2) !important;
        color: var(--MyW) !important;
        /**/
    }
    .ms-DetailsHeader {
        background-color: var(--Myred3) !important;
    }
    span[class*="ms-DetailsHeader-cellName"],
    div[data-automationid="DetailsHeader"] {
        color: var(--MyW) !important;
    }
    div[data-automationid="FieldRenderer-name"],
    .ms-Tile,
    .LeftPane,
    body {
        background-color: var(--Myback) !important;
        color: var(--MyW) !important;
    }
    #c_ln,
    div[class*="od-PlanCard"] {
        background-color: var(--Mygray2) !important;
    }
    div[class*="nameField"],
    .c_clogo,
    #c_cb0,
    #c_header,
    a[role="link"],
    button#allAppsLink,
    #appLauncher {
        background-image: none !important;
        background-color: transparent !important;
    }
    #c_cb0,
    #c_header,
    div[id*="NavHeader"],
    #SuiteNav-0 {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border: none !important;
        background-color: var(--Myred3) !important;
