    li a.c_gl,
    input[type="button"],
    input[type="submit"],
    div.StorageOptions-marketDropdown.od-Dropdown,
    .o365cs-nav-appTileBackground,
    a[class*="contextMenuItem"],
    a.LeftNav-subLink,
    a[role="link"],
    button {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
