    #c_clogoc:hover,
    li.active a.c_gl,
    li a.c_gl:hover,
    input[type="button"]:hover,
    input[type="submit"]:hover,
    div.od-Dropdown:hover,
    .o365cs-nav-appTileBackground:hover,
    a[class*="contextMenuItem"]:hover,
    a.LeftNav-subLink:hover,
    a.LeftNav-subLink[aria-selected="true"],
    a[role="link"]:hover,
    button:hover {
        -moz-appearance: none;
