        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--MyB), -1px -1px 1px var(--Myred2) !important;
    }
    button:active {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border-top: 1px solid var(--Mygreen2) !important;
        border-left: 1px solid var(--Mygreen2) !important;
        border-bottom: 1px solid var(--Mygreen3) !important;
        border-right: 1px solid var(--Mygreen3) !important;
        box-shadow: inset 0px 0px 4px 0px var(--Mygreen3) !important;
        background-color: var(--Mygreen3) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Mygreen1) 0%, transparent 99%), linear-gradient(180deg, rgba(168, 168, 168, 0.5) 48%, hsla(0, 0%, 0%, 0.4) 49%, rgba(0, 0, 0, 0.8) 100%, rgba(168, 168, 168, 0.2)) !important;
        outline: none !important;
        opacity: 1.0 !important;
        color: var(--MyW) !important;
        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--Mygreen3), -1px -1px 1px var(--Myred2) !important;
    }
    body {
        color: var(--Mytext) !important;
    }
    newUI .entry .style-name:hover::before {
        filter: brightness(150%);
        background-color: var(--MyW) !important;
    }
    a,
    a:visited {
        color: var(--Mytext) !important;
    }
    #save-button {
        color: var(--MyB) !important;
    }
    #save-button:hover {
        opacity: .1;
    }
    #save-button:after {
        color: var(--MyB) !important;
        content: 'd' !important;
    }
    body.dirty #save-button {
        color: var(--MyW) !important;
    }
    #apply-all-updates,
    body.dirty #save-button:hover {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        box-shadow: inset 0 0 4px 0 var(--MyB) !important;
        border-bottom-color: var(--MyB) !important;
        border-top-color: var(--Mygray3) !important;
        border-right-color: var(--MyB) !important;
        border-left-color: var(--MyB) !important;
        background-color: var(--Myred3) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Myred1) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        outline: none !important;
        opacity: 1.0 !important;
        color: var(--MyW) !important;
        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--MyB), -1px -1px 1px var(--Mygray1) !important;
    }
    body.dirty #save-button:after {
        color: var(--MyW) !important;
        content: '?' !important;
    }
    #apply-all-updates:hover {
        filter: brightness(130%);
    }
    .newUI .entry:nth-child(2n) {
        background-color: var(--MyB) !important;
    }
    .svg-icon:hover,
    .newUI .entry:hover .svg-icon:hover {
        fill: var(--MyW) !important;
    }
    .newUI .no-update:not(.update-problem):not(.update-done) .up-to-date,
    .newUI .can-update .update {
        color: var(--MyB);
    }
    a.update::after {
        background-color: var(--Mygreen2) !important;
        color: var(--MyB) !important;
        border: 1px solid var(--MyB) !important;
        border-radius: 5px !important;
    }
    a.update::after:hover rect:hover {
        background-color: var(--Mygreen1) !important;
        color: var(--MyB) !important;
        border: 1px solid var(--MyB) !important;
        border-radius: 5px !important;
    }
    .warning {
        color: var(--Myred1) !important;
    }
    #confirm>div {
        background-color: var(--Mygray3);
        border: solid 2px rgba(0, 0, 0, 0.5);
    }
    .CodeMirror-linewidget .applies-to {
        color: var(--Mytext) !important;
        background: var(--Myback) !important;
        border-style: none !important;
    }
    .CodeMirror-linewidget .applies-to>label {
        color: var(--Mytext) !important;
    }
    select#manage.newUI.sort {
        height: 20px !important;
    }
    svg[class="svg-icon checked"] {
        filter: invert(1) !important;
    }
    input[type="checkbox"] {
        background-color: var(--Myback) !important;
        border-color: var(--Myborder) !important;
    }
    body,
    #help-popup,
    #message-box>div,
    #message-box-buttons,
    #message-box-title,
    .dropdown-content,
    #header {
        /*background-image: linear-gradient(to top, rgba(0, 0, 0, 0.8), rgba(41, 41, 41, 0.9), rgba(75, 75, 75, 0.9) 100%, transparent) !important;*/
        background-image: linear-gradient(to top, rgba(20, 20, 20, 0.8), rgba(75, 75, 75, 0.8), rgba(20, 20, 20, 0.8) 100%, transparent) !important;
    }
    select#manage.newUI.sort,
    select,
    #message-box.danger #message-box-title {
        /*background-image: linear-gradient(to top, rgba(65, 21, 21, 0.8), rgba(97, 22, 22, 0.8), rgba(189, 38, 38, 0.8) 100%, transparent) !important;*/
        background-image: linear-gradient(to top, rgba(80, 0, 0, 0.8), rgba(170, 0, 0, 0.8), rgba(80, 0, 0, 0.8) 100%, transparent) !important;
    }
    #options:not([data-pref="manage.options.expanded"]),
    .odd {
        background-image: linear-gradient(to left, rgba(20, 20, 20, 0.8), rgba(75, 75, 75, 0.8), rgba(20, 20, 20, 0.8) 100%, transparent) !important;
    }
    .even {
        background-image: linear-gradient(to left, rgba(80, 0, 0, 0.8), rgba(170, 0, 0, 0.8), rgba(80, 0, 0, 0.8) 100%, transparent) !important;
    }

