    }
    .CodeMirror .cm-overlay,
    .CodeMirror span.cm-attribute {
        color: #FFF700;
    }
    .CodeMirror span.cm-error {
        color: #FF0000;
    }
    .CodeMirror .CodeMirror-activeline-background {
        background: #921111;
    }
}

