/* ==UserStyle==
@name         Twitter Dark
@namespace    https://danamw.github.io/scripts/scripts/TwitterDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/TwitterDark.user.css
@version      0.0.55
@description  My Twitter Dark
@author       Dana Meli
@homepageURL  https://danamw.github.io
@preprocessor default
@var checkbox doStream "Auto Stream Tweets" 1
==/UserStyle== */

@-moz-document regexp("https://twitter.com(?!widget|cards).*"),
regexp("https://mobile.twitter.com(?!widget|cards).*"),
url-prefix("https://t.co"),
url-prefix("https://help.twitter.com"),
domain("twimg.com") {
    html {
        -webkit-tap-highlight-color: var(--MyXW) !important;
    }
    .css-1dbjc4n.r-f8p6my.r-1p0dtai.r-1d2f490.r-1xcajam.r-zchlnj.r-ipm5af,
