/*==UserStyle==
@name         YouTube Dark
@namespace    https://danamw.github.io/scripts/scripts/YoutubeDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/YoutubeDark.user.css
@version      0.0.10
@description  YouTube Tweaks
@author       Dana Meli
@homepageURL  https://danamw.github.io
@preprocessor default
@var range smove "Slide Search Bar"[10, 0, 75, 1, "vw"]
@var range swidth "Resize search input"[750, 100, 1000, 1, "px"]
==/UserStyle== */

@-moz-document domain("youtube.com"),
domain("studio.youtube.com") {
     :not(.style-scope)[hidden] {
        display: initial !important;
    }
    #movie_player,
    #container,
    #player-container-outer,
    #player-container-inner,
    video {
        -moz-user-select: none !important;
        -webkit-user-select: none !important;
    }
    html:not(.style-scope)[dark],
     :not(.style-scope)[dark] {
        --yt-basic-foreground-body-color: hsla(0, 87%, 12%, 0.4) !important;
        --yt-basic-background-color: hsla(0, 87%, 12%, 0.4) !important;
        --yt-std-body-300: hsla(0, 87%, 12%, 0.4) !important;
        --yt-std-surface-200: hsl(0, 0%, 11%);
        --yt-std-surface-300: hsl(0, 0%, 14%);
        --yt-std-surface-400: hsl(0, 0%, 16%);
        --yt-primary-color: hsla(0, 87%, 12%, 0.4) !important;
        --yt-primary-text-color: hsla(0, 100%, 100%, .88);
        --yt-hovered-text-color: hsla(0, 100%, 100%, .74);
        --yt-secondary-text-color: hsla(0, 100%, 100%, .5);
        --yt-tertiary-text-color: hsla(0, 100%, 100%, .5);
        --yt-placeholder-text-color: hsla(0, 100%, 100%, .5);
        --yt-border-color: hsla(0, 100%, 100%, .08);
        --yt-commentbox-border-inactive: hsla(0, 100%, 100%, .5);
        --yt-commentbox-border-active: hsla(0, 100%, 100%, .88);
        --yt-alert-background: hsla(0, 87%, 12%, 0.4) !important;
        --yt-paper-button-ink-color: hsla(0, 0%, 100%, .2);
        --yt-icon-color: hsla(0, 100%, 100%, .5);
        --yt-icon-hover-color: hsla(0, 100%, 100%, .74);
        --yt-icon-active-color: hsla(0, 100%, 100%, .88);
        --yt-icon-disabled-color: hsla(0, 100%, 100%, .26);
        --yt-expand-color: hsla(0, 0%, 100%, .6);
        --yt-channel-owner: hsla(0, 100%, 100%, .88);
        --yt-placeholder-text: hsl(0, 0%, 93.3%);
        --yt-playlist-background-header: hsla(0, 87%, 12%, 0.4) !important;
        --yt-playlist-background-item: hsla(0, 87%, 12%, 0.4) !important;
        --yt-playlist-title-text: hsla(0, 100%, 100%, .88);
        --yt-playlist-message-text: hsl(0, 0%, 93.3%);
        --yt-playlist-message-text-hover: hsla(0, 100%, 100%, .74);
        --yt-button-active-color: hsla(0, 100%, 100%, .88);
        --yt-copyright-text: hsla(0, 0%, 100%, .6);
        --yt-guide-entry-text-color: hsla(0, 100%, 100%, .88);
        --yt-thumbnail-placeholder-color: hsl(0, 0%, 16%);
        --yt-primary-disabled-button-text-color: hsla(0, 0%, 100%, .2);
        --yt-featured-channel-title-text-color: hsla(0, 100%, 100%, .5);
        --yt-formatted-string-deemphasize-color: hsl(0, 0%, 53.3%);
        --yt-video-secondary-info-description-background: hsla(0, 0%, 53.3%, .2);
        --yt-simple-menu-header-background: hsla(0, 87%, 12%, 0.4) !important;
        --yt-item-section-header-color: hsla(0, 87%, 12%, 0.4) !important;
        --yt-menu-hover-backgound-color: hsla(0, 87%, 12%, 0.4) !important;
        --yt-menu-focus-background-color: hsla(0, 87%, 12%, 0.4) !important;
        --yt-searchbox-text-color: var(--yt-primary-text-color);
        --yt-material-searchbox-inactive: hsl(0, 0%, 7%);
        --yt-material-searchbox-active: hsl(0, 0%, 100%);
        --yt-material-searchbox-inactive-shadow: hsla(0, 0%, 0%, .04);
        --yt-material-searchbox-active-shadow: hsla(0, 0%, 0%, .26);
        --yt-material-searchbox-inset: hsla(0, 0%, 0%, .04);
        --yt-material-searchbox-text-color: hsl(0, 0%, 6.7%);
        --yt-app-background: hsl(0, 0%, 7%);
        --yt-main-app-background: hsl(0, 0%, 7%);
        --yt-main-app-background-tmp: hsl(0, 0%, 7%);
        --yt-guide-background: hsl(0, 0%, 11%);
        --yt-dialog-background: hsl(0, 0%, 14%);
        --yt-searchbox-background: hsl(0, 0%, 7%);
        --yt-channel-header-background: hsla(0, 0%, 11%, .8);
        --yt-sidebar-background: hsla(0, 0%, 11%, .8);
        --yt-transcript-background: hsl(0, 0%, 11%);
        --yt-chat-bubble-other-border-color: rgba(255, 255, 255, 0.20);
        --yt-chat-bubble-other-background-color: transparent;
        --yt-chat-bubble-self-border-color: rgba(255, 255, 255, 0.20);
        --yt-chat-bubble-self-background-color: #282828;
    }
    /*
    #items.ytd-grid-renderer {
        width: calc(100% + 5%) !important;
    }
    */
    * {
        text-decoration: none !important;
        scrollbar-width: thin !important;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    ytd-active-account-header-renderer {
        position: relative;
        top: 500px !important;
    }
    .ytd-multi-page-menu-renderer {
        background: var(--Mygray2) !important;
    }
    html {
        overflow: scroll;
        overflow-x: hidden;
        scrollbar-color: var(--Myred1) var(--Myred3);
        scrollbar-width: thin;
    }
    head,
    body,
    html {
        background-image: none !important;
    }
    app-drwer {
        scrollbar-color: var(--Myred1) var(--Myred3);
        scrollbar-width: thin;
    }
    app-drawer {
        scrollbar-color: var(--Myred1) var(--Myred3);
        scrollbar-width: thin;
    }
    template.style-scope.paper-item {
        background-color: transparent !important;
    }
    #creator-page,
    .style-scope.paper-item {
        background-color: var(--Myback) !important;
        color: var(--MyW) !important;
    }
    .sbdd_b,
    .sbsb_a,
    ytd-watch-flexy,
    html {
        background-color: var(--Myback) !important;
        color: var(--MyW) !important;
