/* ==UserStyle==
@name         Stack Overflow Dark
@version      4.4.4
@description  Dark theme for Stack Overflow & most Stack Exchange network sites
@namespace    StylishThemes
@author       StylishThemes
@homepageURL  https://github.com/StylishThemes/StackOverflow-Dark
@supportURL   https://github.com/StylishThemes/StackOverflow-Dark/issues
@updateURL    https://raw.githubusercontent.com/StylishThemes/StackOverflow-Dark/master/stackoverflow-dark.user.css
@license      CC-BY-SA-4.0
@advanced color base-color "Base color scheme" #4183C4
@advanced color visited-color "Visited link color" #4183C4
@advanced color accepted-color "Accepted answer background color" #002810
@advanced color strong-color "Strong color" #ccc
@advanced text font-choice "Code Font" Menlo
@advanced dropdown bg-choice "Background" {
  crosshatch "crosshatch" <<<EOT
  url('https://camo.githubusercontent.com/5b8e4cd1a5ce69b6affa17ebd8e4aee00c147618/68747470733a2f2f7374796c6973687468656d65732e6769746875622e696f2f4769744875622d4461726b2f696d616765732f6261636b67726f756e64732f62672d63726f737368617463682e706e67') EOT;
  uniform "uniform" <<<EOT
  url('https://camo.githubusercontent.com/f83a9a60a2c64be0c88833ccb436e32a458cd2ce/68747470733a2f2f7374796c6973687468656d65732e6769746875622e696f2f4769744875622d4461726b2f696d616765732f6261636b67726f756e64732f62672d74696c652d756e69666f726d2e706e67') EOT;
  random "random" <<<EOT
  url('https://camo.githubusercontent.com/3beb850d8069e424480199a09899030c9e58e85f/68747470733a2f2f7374796c6973687468656d65732e6769746875622e696f2f4769744875622d4461726b2f696d616765732f6261636b67726f756e64732f62672d74696c65312e706e67') EOT;
  custom "custom" <<<EOT
  var(--bg-custom) EOT;
  none "none" <<<EOT
  none EOT;
}
@advanced text bg-custom "Background image [wrap in url()]" "url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAMAAAAMCGV4AAAAD1BMVEUREREREREAAAAREREWFhkN5cwCAAAABHRSTlNfGAAwFBG7CgAAACxJREFUeAFjYGJiZGBhhhMMYIIRTjCgcIE8VC4TKpeRmYEZiTtIzGNihhNMAJJAATdKr5xFAAAAAElFTkSuQmCC')"
@advanced dropdown bg-options "Background image type" {
  Tiled "Tiled" <<<EOT
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important; EOT;
  Fit window size "Fit window size" <<<EOT
  background-repeat: no-repeat !important;
  background-size: cover !important;
  background-position: center top !important; EOT;
}
@advanced dropdown bg-attachment "Background image attachment" {
  Scroll "Scroll" <<<EOT
  scroll EOT;
  Fixed "Fixed" <<<EOT
  fixed EOT;
}
@advanced dropdown syntax-theme "Syntax theme" {
  Desert "Desert" <<<EOT
  /*! Desert *\/pre.prettyprint{display:block;background-color:#333!important}pre .nocode{background-color:none!important;color:#000!important}pre .str,code .str{color:#ffa0a0!important}pre .kwd,code .kwd{color:#f0e68c!important;font-weight:bold!important}pre .com,code .com{color:#87ceeb!important}pre .typ,code .typ{color:#98fb98!important}pre .lit,code .lit{color:#cd5c5c!important}pre .pun,code .pun{color:#fff!important}pre .pln,code .pln{color:#fff!important}pre .tag,code .tag{color:#f0e68c!important;font-weight:bold!important}pre .atn,code .atn{color:#bdb76b!important;font-weight:bold!important}pre .atv,code .atv{color:#ffa0a0!important}pre .dec,code .dec{color:#98fb98!important}ol.linenums{margin-top:0;margin-bottom:0;color:#aeaeae}li.L0,li.L1,li.L2,li.L3,li.L5,li.L6,li.L7,li.L8{list-style-type:none!important}@media print{pre.prettyprint{background-color:none!important}pre .str,code .str{color:#060!important}pre .kwd,code .kwd{color:#006!important;font-weight:bold!important}pre .com,code .com{color:#600!important;font-style:italic!important}pre .typ,code .typ{color:#404!important;font-weight:bold!important}pre .lit,code .lit{color:#044!important}pre .pun,code .pun{color:#440!important}pre .pln,code .pln{color:#000!important}pre .tag,code .tag{color:#006!important;font-weight:bold!important}pre .atn,code .atn{color:#404!important}pre .atv,code .atv{color:#060!important}} EOT;
  Atelier-cave-dark "Atelier-cave-dark" <<<EOT
  /*! Atelier-cave-dark *\/.prettyprint{background:#19171c!important;font-family:Menlo,"Bitstream Vera Sans Mono","DejaVu Sans Mono",Monaco,Consolas,monospace!important;border:0!important}.pln{color:#efecf4!important}ol.linenums{margin-top:0!important;margin-bottom:0!important;color:#655f6d!important}li.L0,li.L1,li.L2,li.L3,li.L4,li.L5,li.L6,li.L7,li.L8,li.L9{padding-left:1em!important;background-color:#19171c!important;list-style-type:decimal!important}@media screen{.str{color:#2a9292!important}.kwd{color:#955ae7!important}.com{color:#655f6d!important}.typ{color:#576ddb!important}.lit{color:#aa573c!important}.pun{color:#efecf4!important}.opn{color:#efecf4!important}.clo{color:#efecf4!important}.tag{color:#be4678!important}.atn{color:#aa573c!important}.atv{color:#398bc6!important}.dec{color:#aa573c!important}.var{color:#be4678!important}.fun{color:#576ddb!important}} EOT;
  Atelier-dune-dark "Atelier-dune-dark" <<<EOT
  /*! Atelier-dune-dark *\/.prettyprint{background:#20201d!important;font-family:Menlo,"Bitstream Vera Sans Mono","DejaVu Sans Mono",Monaco,Consolas,monospace!important;border:0!important}.pln{color:#fefbec!important}ol.linenums{margin-top:0!important;margin-bottom:0!important;color:#7d7a68!important}li.L0,li.L1,li.L2,li.L3,li.L4,li.L5,li.L6,li.L7,li.L8,li.L9{padding-left:1em!important;background-color:#20201d!important;list-style-type:decimal!important}@media screen{.str{color:#60ac39!important}.kwd{color:#b854d4!important}.com{color:#7d7a68!important}.typ{color:#6684e1!important}.lit{color:#b65611!important}.pun{color:#fefbec!important}.opn{color:#fefbec!important}.clo{color:#fefbec!important}.tag{color:#d73737!important}.atn{color:#b65611!important}.atv{color:#1fad83!important}.dec{color:#b65611!important}.var{color:#d73737!important}.fun{color:#6684e1!important}} EOT;
  Atelier-estuary-dark "Atelier-estuary-dark" <<<EOT
  /*! Atelier-estuary-dark *\/.prettyprint{background:#22221b!important;font-family:Menlo,"Bitstream Vera Sans Mono","DejaVu Sans Mono",Monaco,Consolas,monospace!important;border:0!important}.pln{color:#f4f3ec!important}ol.linenums{margin-top:0!important;margin-bottom:0!important;color:#6c6b5a!important}li.L0,li.L1,li.L2,li.L3,li.L4,li.L5,li.L6,li.L7,li.L8,li.L9{padding-left:1em!important;background-color:#22221b!important;list-style-type:decimal!important}@media screen{.str{color:#7d9726!important}.kwd{color:#5f9182!important}.com{color:#6c6b5a!important}.typ{color:#36a166!important}.lit{color:#ae7313!important}.pun{color:#f4f3ec!important}.opn{color:#f4f3ec!important}.clo{color:#f4f3ec!important}.tag{color:#ba6236!important}.atn{color:#ae7313!important}.atv{color:#5b9d48!important}.dec{color:#ae7313!important}.var{color:#ba6236!important}.fun{color:#36a166!important}} EOT;
  Atelier-forest-dark "Atelier-forest-dark" <<<EOT
