/* ==UserStyle==
@name         123rf Dark
@namespace    https://danamw.github.io/scripts/scripts/123rfDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/123rfDark.user.css
@version      0.0.8
@author       Dana L. Meli-Wischman
@description  Dark Style.
@homepageURL  https://DanaMW.github.io
==/UserStyle== */

@-moz-document domain("123rf.com") {
    #main-wrapper-searchresult,
    .adv-search-container,
    .ui.fluid.container,
    div[class="kwcluster-container"],
    .header-bar-left,
    #quotaTypeInfo,
    html,
    body {
        background-color: var(--Myback) !important;
        color: var(--MyW) !important;
    }
    * {
        text-decoration: none !important;
        scrollbar-color: var(--Myred1) var(--Myred3);
        scrollbar-width: thin !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    input {
        background-color: var(--Mygray3) !important;
        color: var(--MyW) !important;
    }
    div[class="ui label margin-mini vertical-bottom horizontal-right"],
    .kwcluster-chip,
    button {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border: none;
        color: var(--Mytext) !important;
        background-color: var(--MyB) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, hsl(0, 0%, 0%) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        text-shadow: 1px 1px 0 var(--Myred1) !important;
        -moz-transition: border-color .3s ease, box-shadow .7s ease !important;
        -webkit-transition: border-color .3s ease, box-shadow .7s ease !important;
        transition: border-color .3s ease, box-shadow .7s ease !important;
        transition-timing-function: linear !important;
        box-shadow: 0 1px 4px 1px var(--MyB) !important;
    }
    div[class="ui label margin-mini vertical-bottom horizontal-right"]:hover,
    .kwcluster-chip:hover,
    button:hover {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        box-shadow: inset 0px 0px 4px 0px var(--MyB) !important;
        background-color: var(--MyX1) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Myred1) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        outline: none !important;
        opacity: 1.0 !important;
        color: var(--MyW) !important;
        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--MyB), -1px -1px 1px var(--Myred2) !important;
    }
    div[class="ui label margin-mini vertical-bottom horizontal-right"]:active,
    .kwcluster-chip:active,
    button:active {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        box-shadow: inset 0px 0px 4px 0px var(--MyB) !important;
        background-color: var(--MyX1) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Myred1) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        outline: none !important;
        opacity: 1.0 !important;
        color: var(--MyW) !important;
        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--MyB), -1px -1px 1px var(--Myred2) !important;
    }
    .row.header-one.desktop-view {
        background-color: var(--Myred3) !important;
        color: var(--MyW) !important;
    }
    .srchbar-wrapper {
        background-color: var(--Mygray2) !important;
    }
}

/* ==UserStyle==
@name         AdiIrc Dark
@namespace    https://danamw.github.io/scripts/scripts/AdiircDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/AdiircDark.user.css
@version      0.0.12
@author       Dana L. Meli-Wischman
@description  Dark Style.
@homepageURL  https://DanaMW.github.io
==/UserStyle== */

@-moz-document domain("adiirc.com"),
domain("dev.adiirc.com") {
    html,
    body {
        color: var(--MyW) !important;
    }
    tr>th,
    tr {
        background-color: var(--Mygray2) !important;
    }
    tr.context-menu-selection {
        background-color: var(--MyX1) !important;
    }
    html,
    body,
    pre,
    .wiki.wiki-page,
    #content,
    #main {
        background-color: var(--MyB) !important;
    }
    * {
        text-decoration: none !important;
        scrollbar-width: thin !important;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    tr.context-menu-selection:hover,
    #header {
        background-color: var(--Myred2) !important;
    }
    #top-menu {
        background-color: var(--Myred3) !important;
    }
    a {
        color: var(--MyW) !important;
    }
    span,
     ::marker,
    strong a,
    strong,
    h1,
    h3 {
        color: var(--Myred1) !important;
    }
    li {
        color: var(--Mygreen1) !important;
    }
    .post,
    #right,
    #footer,
    #left,
    #wrapper,
    li>a {
        background-image: none !important;
        background-color: transparent !important;
    }
    li>a.wiki.selected,
    li>a:hover {
        background-image: -moz-linear-gradient(top, var(--Myred1), var(--Myred3)) !important;
        color: var(--MyW) !important;
    }
    .issue {
        color: var(--MyB) !important;
    }
    .members.box,
    select:hover,
    #project-jump>span:hover,
    .drdn-items.all-projects.selection a:hover,
    #sidebar {
        background-image: linear-gradient(to top, rgba(65, 21, 21, 0.8), rgba(97, 22, 22, 0.8), rgba(189, 38, 38, 0.8) 100%, transparent) !important;
    }
    .issue,
    .news.box,
    .issues.box,
    select,
    #context-menu>ul,
    .drdn-content,
    #project-jump>span {
        background-image: linear-gradient(to top, rgba(0, 0, 0, 0.8), rgba(41, 41, 41, 0.9), rgba(75, 75, 75, 0.9) 100%, transparent) !important;
    }
    select,
    #project-jump>span {
        color: var(--MyW) !important;
    }
    option,
    input {
        background-color: var(--Mygray3) !important;
        color: var(--MyW) !important;
        text-align: center !important;
    }
    .progress {
        border: 1px solid var(--MyW) !important;
    }
    .todo {
        background-color: var(--Myred3) !important;
        color: var(--MyW) !important;
    }
    .progress .closed {
        background-color: var(--Myred1) !important;
    }
    td.buttons a.icon-only.icon-actions.js-contextmenu {
        color: var(--Mygreen1) !important;
    }
    html,
    body {
        background-color: var(--MyB) !important;
    }
    .stock {
        display: none !important;
    }
    * {
        text-decoration: none !important;
        scrollbar-width: thin !important;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }
     ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }
     ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }
     ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }
     ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }
    p,
    a {
        color: var(--MyW) !important;
        background-color: transparent !important;
    }
    a:hover {
        color: var(--Myred1) !important;
        text-decoration: none !important;
    }
    h1 {
        color: var(--TwitterBlue) !important;
    }
    h2 {
        color: var(--TwitterRed) !important;
    }
    small {
        color: var(--TwitterPink) !important;
    }
    legend,
    h3,
    h4,
    h5,
    strong {
        color: var(--MyW) !important;
    }
    button {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border: none !important;
        color: var(--Mytext) !important;
        background-color: var(--MyB) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, hsl(0, 0%, 0%) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        text-shadow: 1px 1px 0 var(--Myred1) !important;
        -moz-transition: border-color .3s ease, box-shadow .7s ease !important;
        -webkit-transition: border-color .3s ease, box-shadow .7s ease !important;
        transition: border-color .3s ease, box-shadow .7s ease !important;
        transition-timing-function: linear !important;
        box-shadow: 0 1px 4px 1px var(--MyB) !important;
    }
    button:hover {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border: none !important;
        box-shadow: inset 0px 0px 4px 0px var(--MyB) !important;
        background-color: var(--Myred3) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Myred1) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        outline: none !important;
        opacity: 1.0 !important;
        color: var(--MyW) !important;
        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--MyB), -1px -1px 1px var(--Myred2) !important;
    }
    button:active {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border: none !important;
        box-shadow: inset 0px 0px 4px 0px var(--Mygreen3) !important;
        background-color: var(--Mygreen3) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Mygreen1) 0%, transparent 99%), linear-gradient(180deg, rgba(168, 168, 168, 0.5) 48%, hsla(0, 0%, 0%, 0.4) 49%, rgba(0, 0, 0, 0.8) 100%, rgba(168, 168, 168, 0.2)) !important;
        outline: none !important;
        opacity: 1.0 !important;
        color: var(--MyW) !important;
        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--Mygreen3), -1px -1px 1px var(--Myred2) !important;
    }
    .stock {
        background-image: none !important;
        background-color: transparent !important;
    }
    header,
    .stock_navbar {
        background-color: var(--Myred3) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Myred1) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
    }
    ul.toc.right {
        background-color: var(--Mygray1) !important;
        background-image: linear-gradient(to top, rgba(0, 0, 0, 0.8), rgba(41, 41, 41, 0.9), rgba(75, 75, 75, 0.9) 100%, transparent) !important;
    }
    .box {
        background-color: var(--Myred3) !important;
        background-image: linear-gradient(to top, rgba(65, 21, 21, 0.8), rgba(97, 22, 22, 0.8), rgba(189, 38, 38, 0.8) 100%, transparent) !important;
    }
    textarea {
        background-color: var(--Mygray3) !important;
        color: var(--MyW) !important;
        border-radius: 15px !important;
        border: 1px solid var(--Myred1) !important;
    }
    input {
        background-color: var(--Mygray3) !important;
        color: var(--MyW) !important;
        border-radius: 15px !important;
        border: 1px solid var(--Myred1) !important;
        text-align: center !important;
    }
    .tooltip {
        background-color: var(--MyX1) !important;
        opacity: 0.4;
        color: var(--MyW) !important;
        padding: 8px;
        border-radius: 2px;
        border: 1px solid var(--MyW) !important;
    }
    #footer {
        display: none !important;
        background-color: var(--Myred3) !important;
        background-image: none !important;
        border: none !important;
        box-shadow: none !important;
    }
}

/* ==UserStyle==
@name         Amazon Dark
@namespace    https://danamw.github.io/scripts/scripts/AmazonDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/AmazonDark.user.css
@version      0.0.53
@description  Amazon Dark
@author       Dana L. Meli-Wischman
@homepageURL  https://DanaMW.github.io
==/UserStyle== */

@-moz-document domain("amazon.com") {

    *,
    html {
        scrollbar-color: var(--Myred1) var(--Myred3);
        scrollbar-width: thin;
        text-decoration: none !important;
    }

    ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }

    ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }

    ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }

    ::marker {
        color: var(--Mygreen1) !important;
    }

    li,
    b,
    br {
        color: var(--Mygray1) !important;
    }

    #sw-atc-details-single-container,
    .a-container,
    #sw-full-view-container {
        background-image: none !important;
        background-color: transparent !important;
    }

    .a-page,
    #a-page,
    #sw-full-view-container,
    .a-container,
    .a-row,
    #dp,
    .stores-page,
    .avu-content-aui,
    .bg-hero,
    div[role="main"],
    .a-row #listPrivacy,
    html,
    #pageContent,
    body {
        background-color: var(--MyB);
        color: var(--MyW)
    }

    .nav-searchbar,
    .rhf-border {
        border-color: transparent !important
    }

    div[class="a-row a-spacing-medium widgetLink-group"],
    [class="a-row a-spacing-medium"],
    .nav-search-field,
    .nav-searchbar {
        background-color: transparent !important;
    }

    input:not([type="submit"]) {
        background-color: var(--Mygray3) !important;
        color: var(--MyW) !important;
        text-align: center !important;
        border-radius: 15px !important;
        border: 1px solid var(--MyW) !important;
    }

    input[name="submit.addToCart"] {
        padding: 0px !important;
    }

    *|*:any-link:not(svg|a) {
        text-decoration: none !important;
    }

    div[class*="nav-timeline"],
    div[id*="nav-flyout"],
    td[class*="comparison"],
    tr[class*="comparison"],
    tr[id*="comparison"] {
        background-color: var(--Mygray3) !important;
        color: var(--MyW) !important;
    }

    li.selected[role="tab"] .a-row,
    a:link,
    *|*:link {
        color: rgb(0, 0, 238);
        text-decoration: none !important;
    }

    .milestone-bar_background {
        background-color: transparent !important;
    }

    .hmenu-item.hmenu-title {
        color: var(--Mygreen1) !important;
    }

    .milestone-marker::before,
    .milestone-markers::after {
        background-color: var(--Mygreen1) !important;
    }

    .card-lite.product-shoveler,
    #promise-background-container {
        background-color: transparent !important;
    }

    #cbcc_banner,
    .rhf-divider-no-gradient {
        border-color: var(--Myred1) !important;
    }

    .nav-timeline-dot.nav-timeline-icon {
        background-color: var(--Myred1) !important;
    }

    .rhf-header,
    .nav-title {
        color: var(--Myred1) !important;
    }

    h2[class*="ya-card__heading"] {
        color: var(--Mygreen1) !important;
    }

    ul>li>a,
    ul.a-pagination li.a-normal,
    .nav-search-scope.nav-sprite {
        background-image: none !important;
    }

    td.prodDetAttrValue,
    .a-button-text,
    .nav-search-submit-text,
    .fresh-shoveler .as-title-block .a-color-base,
    .a-size-base.a-color-secondary,
    span.description,
    .a-size-small {
        color: var(--MyW) !important;
    }

    [class*="tablemodule"] {
        background-color: var(--Mygray3) !important;
    }

    .a-box:not(.a-box.delivery-box) .a-box-inner,
    .s-pagination-item,
    a.nav-a,
    li[role="tab"],
    a[class*="style__button"],
    ul>li>a:not([style]),
    #nav-hamburger-menu,
    .a-disabled,
    .a-normal>a,
    .a-last>a,
    .nav-search-scope.nav-sprite,
    .wl-invite-button-contents,
    input[type="submit"]:not(.a-button-text):not(.a-button-input),
    .feed-left,
    .feed-right,
    .a-button-inner,
    button {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        border: none;
        color: var(--Mytext) !important;
        background-color: var(--MyB) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, hsl(0, 0%, 0%) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        text-shadow: 1px 1px 0 var(--Myred1) !important;
        -moz-transition: border-color .3s ease, box-shadow .7s ease !important;
        -webkit-transition: border-color .3s ease, box-shadow .7s ease !important;
        transition: border-color .3s ease, box-shadow .7s ease !important;
        transition-timing-function: linear !important;
        box-shadow: 0 1px 4px 1px var(--MyB) !important;
    }

    #a-page[aria-hidden="true"] {
        background-color: var(--MyXB) !important;
    }

    .a-disabled {
        opacity: 0.5 !important;
    }

    .wl-invite-button-contents:hover {
        filter: invert(0) !important;
    }

    .a-box:not(.a-box.delivery-box):not([id="my-lists-tab"]) .a-box-inner:hover,
    .s-suggestion:hover,
    .s-pagination-item:hover,
    .s-pagination-item.s-pagination-selected,
    .s-suggestion.is-hovered,
    a.nav-a:hover,
    .a-link-normal.item:hover,
    li.selected[role="tab"],
    a[class*="style__button"]:hover,
    ul>li>a:hover:not([style]),
    #nav-hamburger-menu:hover,
    a.hmenu-item:hover,
    .a-normal>a:hover,
    .a-last>a:hover,
    .a-selected>a,
    .nav-search-scope.nav-sprite:hover,
    li.a-spacing-micro:hover,
    a.nav-link.nav-item:hover,
    .wl-invite-button-contents:hover,
    input[type="submit"]:hover,
    .feed-left:hover:not(.feed-control-disabled),
    .feed-right:hover:not(.feed-control-disabled),
    .a-button-inner:hover,
    button:hover {
        -moz-appearance: none;
        -webkit-appearance: none;
        appearance: none;
        box-shadow: inset 0px 0px 4px 0px var(--MyB) !important;
        background-color: var(--Myred1) !important;
        background-image: radial-gradient(ellipse farthest-corner at center bottom, var(--Myred1) 0%, transparent 99%), linear-gradient(180deg, hsla(0, 0%, 66%, .5) 48%, rgba(0, 0, 0, .4) 49%, rgba(0, 0, 0, .8) 100%, hsla(0, 0%, 66%, .2)) !important;
        outline: none !important;
        opacity: 1.0 !important;
        color: var(--MyW) !important;
        -moz-transition: background .2s, box-shadow .4s, border-color .2s !important;
        -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition: background .2s, box-shadow .4s, border-color .2s !important;
        transition-timing-function: linear !important;
        text-shadow: 1px 1px 1px var(--MyB), -1px -1px 1px var(--Myred2) !important;
    }

    .a-box:not(.a-box.delivery-box) .a-box-inner:active,
    .s-pagination-item:active,
    a.nav-a:active,
    li.selected[role="tab"]:active,
    li[role="tab"]:active,
    a[class*="style__button"]:active,
    ul>li>a:active:not([style]),
    #nav-hamburger-menu:active,
    a.hmenu-item:active,
    .a-normal>a:active,
    .a-last>a:active,
