	}
	::-moz-scrollbar-thumb,
	::-webkit-scrollbar-thumb {
		border-radius: 15px !important;
		transition: all 0.4s !important;
	}
	::-moz-scrollbar-thumb[orient="vertical"]:hover,
	::-webkit-scrollbar-thumb[orient="vertical"]:hover,
	::-moz-scrollbar-thumb[orient="horizontal"]:hover,
	::-webkit-scrollbar-thumb[orient="horizontal"]:hover {
