/* ==UserStyle==
@name         TweetDeck Dark
@namespace    https://danamw.github.io/scripts/scripts/TweetdeckDark.user.css
@updateURL    https://raw.githubusercontent.com/danamw/danamw.github.io/master/scripts/scripts/TweetdeckDark.user.css
@description  A Dark TweetDeck Script
@version      0.1.10
@author       Dana L. Meli-Wischman
@homepageURL  https://danamw.github.io
==/UserStyle==*/

@-moz-document domain("tweetdeck.twitter.com"),
url-prefix("https://pbs.twimg.com/"),
url-prefix("https://abs.twimg.com/"),
url-prefix("https://ton.twimg.com/tweetdeck-web/"),
url-prefix("https://twitter.com/i/safety/") {

    li[class*="stream-item"] [class*="promoted"],
    div[class="stream suggested-tweet-stream permalink-replies"] {
        display: none !important;
    }

    .js-app-columns-container.app-columns-container,
    iframe.r-1yadl64.r-16y2uox,
    iframe.r-1yadl64.r-16y2uox body,
    .js-column-content.column-content,
    html,
    body {
        background-image: none !important;
        background-color: var(--MyB) !important;
    }

    * {
        outline: none !important;
        text-decoration: none !important;
        scrollbar-width: thin;
        scrollbar-color: var(--Myred1) var(--Myred3) !important;
    }

    ::-webkit-scrollbar {
        background: var(--Myred3) !important;
    }

    ::-webkit-scrollbar-thumb {
        background: var(--Myred1) !important;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: var(--Mygreen1) !important;
    }

    ::-webkit-scrollbar-thumb:active {
        background: var(--Mygreen2) !important;
    }

    input,
    select {
        background-color: var(--Mygray3) !important;
        color: var(--MyW) !important;
        text-align: center !important;
        border-radius: 25px !important;
        border: 1px solid var(--Myred1) !important;
    }

    .padding-a--12.border-divider.flex:hover,
    a.list-twitter-list:hover,
    li.list-listmember:hover {
        background-color: var(--MyX1) !important;
    }

    #column-navigator {
        scrollbar-width: none;
    }

    Notification-body {
        color: var(--MyW) !important;
    }

    section.js-column.column {
        margin-right: 0px !important;
        margin-left: 0px !important;
    }

    section.js-column.column.is-focused {
        margin-right: 5px !important;
        margin-left: 5px !important;
        box-shadow: 5px 5px var(--Mygreen1), -5px -5px var(--Mygreen1) !important;
    }

    .js-app-header.pin-all.app-header.is-condensed {
        margin-left: 0px !important;
        margin-right: -5px !important;
        padding-right: 0px !important;
    }

    .js-app-columns.app-columns.horizontal-flow-container.without-tweet-drag-handles {
        padding-left: 0px !important;
        margin-left: 1px !important;
    }

    b[class="fullname inline-block link-complex-target fullname-badged  badge-verified position-rel txt-ellipsis"],
    b[class="fullname inline-block link-complex-target fullname-badged  badge-verified position-rel txt-ellipsis"]::before {
        height: 25px !important;
    }

    .css-1dbjc4n.r-1awozwy.r-1p0dtai.r-1777fci.r-1d2f490.r-ywje51.r-u8s1d.r-zchlnj.r-ipm5af,
    .css-901oao.r-daml9f.r-n6v787.r-19jlu03.r-285fr0.r-q4m81j {
        z-index: 10000 !important;
        background: transparent !important;
    }

    .more-tweets-glow {
        max-width: 100%;
        height: 6px !important;
        margin-top: -6px !important;
        transition: width 200ms ease-in-out;
        background-image: radial-gradient(ellipse farthest-corner at 50% 100%, rgb(255, 0, 0) 0%, rgb(255, 0, 0) 25%, rgba(255, 255, 255, 0) 75%) !important;
        background-color: var(--Mygray3) !important;
    }

    div[role="progressbar"] {
        z-index: 10000 !important;
